{
  "textFieldWrapper": {
    "width": "300px",
    "height": "40px",
    "border": "2px solid black",
    "borderRadius": "4px",
    "backgroundColor": "white",
    "display": "flex",
    "alignItems": "center",
    "paddingLeft": "10px",
    "fontSize": "16px",
    "color": "black",
    "position": "relative"
  },
  "textCursor": {
    "width": "2px",
    "height": "70%",
    "backgroundColor": "black",
    "position": "absolute",
    "left": "12px",
    "animation": "blink 1s step-start 0s infinite"
  },
  "@keyframes blink": {
    "0%": { "opacity": "1" },
    "50%": { "opacity": "0" },
    "100%": { "opacity": "1" }
  }
}

.video-component {
  width: 400px;
  height: 300px;
  border: 4px solid black; /* Outer video frame */
  background-color: white;
  position: relative;
  display: flex;
  justify-content: center;
  align-items: center;
  border-radius: 12px;
  overflow: hidden;
}

/* Top clapboard bar */
.video-component .clapboard {
  width: 100%;
  height: 50px;
  background-color: lightgray;
  border-bottom: 2px solid black;
  display: flex;
  justify-content: space-evenly;
  align-items: center;
}

/* Diagonal lines on the clapboard */
.video-component .clapboard div {
  width: 30px;
  height: 100%;
  background-color: black;
  clip-path: polygon(0 0, 100% 0, 0% 100%, 0 100%);
}

/* Play button */
.video-component .play-button {
  width: 0;
  height: 0;
  border-left: 40px solid black;
  border-top: 25px solid transparent;
  border-bottom: 25px solid transparent;
  position: absolute;
}

.hamburger {
  width: 35px;
  height: 25px;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  cursor: pointer;
}

.hamburger div {
  height: 6px;
  background-color: black;
  border-radius: 10px;
  transition: all 0.3s ease;
}

/* Container for the radio button */
.radio-button {
  width: 24px;
  height: 24px;
  border: 4px solid black; /* Outer ring */
  border-radius: 50%;       /* Makes it circular */
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: white;  /* Background behind inner dot */
}

/* Inner filled circle (selected state) */
.radio-button .inner-circle {
  width: 12px;
  height: 12px;
  background-color: black;  /* Inner dot color */
  border-radius: 50%;       /* Makes it circular */
}

.bullet-list {
  display: flex;
  flex-direction: column;
  gap: 12px;
}

.bullet-item {
  display: flex;
  align-items: center;
  gap: 12px;
}

.bullet-circle {
  width: 20px;
  height: 20px;
  background-color: black;
  border-radius: 50%;
}

.bullet-bar {
  width: 160px;
  height: 20px;
  background-color: black;
  border-radius: 10px;
}

{
  "toggleSwitchWrapper": {
    "width": "50px",
    "height": "25px",
    "backgroundColor": "lightgray",
    "borderRadius": "25px",
    "position": "relative",
    "cursor": "pointer",
    "transition": "background-color 0.3s"
  },
  "toggleSwitchKnob": {
    "width": "23px",
    "height": "23px",
    "backgroundColor": "white",
    "borderRadius": "50%",
    "position": "absolute",
    "top": "1px",
    "left": "1px",
    "transition": "left 0.3s"
  },
  "toggleSwitchWrapperOn": {
    "backgroundColor": "green"
  },
  "toggleSwitchKnobOn": {
    "left": "26px"
  }
}

.image-component {
  width: 400px;
  height: 300px;
  border: 4px solid black; /* Outer frame */
  background-color: white;
  position: relative;
  display: flex;
  justify-content: center;
  align-items: center;
}

/* Inner image frame */
.image-component .inner-frame {
  width: 90%;
  height: 80%;
  border: 2px solid black;
  position: relative;
  background-color: transparent;
}

/* Mountains (as triangles) */
.image-component .mountain1,
.image-component .mountain2 {
  position: absolute;
  bottom: 0;
  border-style: solid;
}

/* Left mountain */
.image-component .mountain1 {
  left: 10%;
  border-width: 0 40px 60px 40px;
  border-color: transparent transparent black transparent;
}

/* Right mountain */
.image-component .mountain2 {
  left: 35%;
  border-width: 0 50px 80px 50px;
  border-color: transparent transparent black transparent;
}

/* Sun (circle) */
.image-component .sun {
  position: absolute;
  top: 10%;
  right: 10%;
  width: 30px;
  height: 30px;
  background-color: black;
  border-radius: 50%;
}

.image-caption-wrapper {
  width: 400px;
  height: 350px; /* Reference size */
  border: 2px solid black;
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 10px;
  background-color: white;
  box-shadow: 2px 2px 8px gray;
}

.caption-image {
  width: 100%;
  height: 250px;
  object-fit: cover;
  border-bottom: 1px solid black;
}

.caption-text {
  padding-top: 10px;
  font-size: 16px;
  text-align: center;
  color: black;
}

.spinner {
  width: 100px;
  height: 100px;
  border: 8px solid lightgray;
  border-top: 8px solid black;
  border-radius: 50%;
  animation: spin 1s linear infinite;
  margin: auto;
}

/* Spin Animation */
@keyframes spin {
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
}

.progress {
  width: 400px;
  height: 30px;
  background-color: navy;
  border-radius: 5px;
  overflow: hidden;
  position: relative;
}

/* Progress Fill */
.progress::before {
  content: "";
  display: block;
  height: 100%;
  width: 25%; /* example fill */
  background-color: lightblue;
}

.clock {
  width: 300px;
  height: 300px;
  border: 8px solid black;
  border-radius: 50%;
  position: relative;
  background-color: white;
}

/* Center dot */
.clock::before {
  content: "";
  width: 10px;
  height: 10px;
  background-color: black;
  border-radius: 50%;
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  z-index: 10;
}

/* Hour Hand */
.clock .hour {
  width: 6px;
  height: 70px;
  background-color: black;
  position: absolute;
  top: 80px;
  left: 147px;
  transform-origin: bottom center;
  transform: rotate(0deg); /* Set dynamically */
  z-index: 5;
}

/* Minute Hand */
.clock .minute {
  width: 4px;
  height: 100px;
  background-color: black;
  position: absolute;
  top: 50px;
  left: 148px;
  transform-origin: bottom center;
  transform: rotate(0deg); /* Set dynamically */
  z-index: 6;
}

/* Tick marks */
.clock .tick {
  width: 2px;
  height: 10px;
  background-color: black;
  position: absolute;
  top: 0;
  left: 149px;
  transform-origin: bottom center;
}

.guage { 
  position: relative; 
  width: 250px; 
  height: 125px; 
  border-top-left-radius: 125px; 
  border-top-right-radius: 125px; 
  background-color: transparent; 
