.sun-ray:nth-child(3) { transform: rotate(90deg) translateX(-50%); top: 50%; left: 100%; }
.sun-ray:nth-child(4) { transform: rotate(135deg) translateX(-50%); top: 80%; left: 80%; }
.sun-ray:nth-child(5) { transform: rotate(180deg) translateX(-50%); top: 100%; left: 50%; }
.sun-ray:nth-child(6) { transform: rotate(225deg) translateX(-50%); top: 80%; left: 20%; }
.sun-ray:nth-child(7) { transform: rotate(270deg) translateX(-50%); top: 50%; left: 0%; }
.sun-ray:nth-child(8) { transform: rotate(315deg) translateX(-50%); top: 10%; left: 20%; }

.compass {
  width: 300px;
  height: 300px;
  border-radius: 50%;
  background-color: white;
  border: 6px solid black;
  position: relative;
  display: flex;
  align-items: center;
  justify-content: center;
}

/* Outer Ring */
.compass::before {
  content: "";
  position: absolute;
  width: 90%;
  height: 90%;
  border-radius: 50%;
  border: 4px solid dimgray;
  box-sizing: border-box;
}

/* Direction Labels */
.direction {
  position: absolute;
  font-weight: bold;
  font-size: 18px;
  color: black;
}

.direction.n { top: 8px; left: 50%; transform: translateX(-50%); }
.direction.s { bottom: 8px; left: 50%; transform: translateX(-50%); }
.direction.e { right: 8px; top: 50%; transform: translateY(-50%); }
.direction.w { left: 8px; top: 50%; transform: translateY(-50%); }

/* Needles */
.needle {
  position: absolute;
  width: 4px;
  height: 45%;
  background-color: black;
  top: 50%;
  left: 50%;
  transform-origin: bottom center;
}

.needle.n { transform: translate(-50%, -100%) rotate(0deg); background-color: white; }
.needle.s { transform: translate(-50%, -100%) rotate(180deg); }
.needle.e { transform: translate(-50%, -100%) rotate(90deg); background-color: white; }
.needle.w { transform: translate(-50%, -100%) rotate(-90deg); }

